import { SAVE_MOVIES } from './types'
import { saveMovies } from './actions'

const initialState = []

export const moviesReducer = (action, state = initialState) => {
  switch (action.type) {
    case SAVE_MOVIES:
      return action.newItems
    default:
      return state
  }
}
