import { CREATE_LIST_ITEM, SAVE_MOVIES } from './types';

export function createListItem({ id, title_english, medium_cover_image, summary }) {
  return {
    type: CREATE_LIST_ITEM,
    id,
    title_english,
    medium_cover_image,
    summary,
  };
}

export function saveMovies(newItems) {
  return {
    type: SAVE_MOVIES,
    newItems,
  };
}
