import { React, useState } from 'react';
import { Menu } from '@mui/icons-material';
import {
  AppBar,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  IconButton,
  TextField,
  Toolbar,
  Typography,
} from '@mui/material';
import { Box } from '@mui/system';
import { useForm } from 'react-hook-form';
import './AuthStyle.css';

const AuthComponent = () => {
  const [errorLogin, setErrorLogin] = useState(false);
  const [textErrorLogin, setTextErrorLogin] = useState('');
  const [errorPassword, setErrorPassword] = useState(false);
  const [openAuth, setOpenAuth] = useState(false);
  const [openSign, setOpenSign] = useState(false);

  const {
    register,
    control,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm();

  const handleClickOpenAuth = () => {
    setOpenAuth(true);
  };

  const handleClickCloseAuth = () => {
    setOpenAuth(false);
  };

  const handleClickOpenSign = () => {
    setOpenSign(true);
  };

  const handleClickCloseSign = () => {
    setOpenSign(false);
  };

  const clickSubmit = () => {
    if (errors) {
      setErrorLogin(true);
      setTextErrorLogin('Должно быть не менее 5 символов');
    }
  };

  const onSubmit = (data) => {
    alert(JSON.stringify(data));
    reset();
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="open drawer"
            sx={{ mr: 2 }}
          >
            <Menu />
          </IconButton>
          <Typography
            variant="h5"
            noWrap
            component="div"
            sx={{ flexGrow: 1, display: { xs: 'none', sm: 'block' } }}
          >
            Vladik's films
          </Typography>
          <Button color="inherit" variant="outlined" onClick={handleClickOpenAuth}>
            Log In
          </Button>

          <Dialog
            open={openAuth}
            onClose={handleClickCloseAuth}
            aria-labelledby="form-dialog-title"
          >
            <form>
              <DialogTitle id="form-dialog-title">Authorization</DialogTitle>
              <DialogContent>
                <Controller
                  name={'textValue'}
                  control={control}
                  render={({ field: { onChange, value } }) => (
                    <TextField
                      error={errorLogin}
                      color="primary"
                      margin="dense"
                      id="name"
                      onChange={onChange}
                      value={value}
                      label="Log In"
                      type="login"
                      helperText={textErrorLogin}
                      fullWidth
                      {...register('username', {
                        required: true,
                        minLength: 5,
                        maxLength: 20,
                      })}
                    />
                  )}
                />

                <TextField margin="dense" id="pass" label="Password" type="password" fullWidth />
              </DialogContent>

              <DialogActions>
                <Button onClick={handleClickCloseAuth} color="primary">
                  Cancel
                </Button>
                <Button onClick={handleSubmit(onSubmit)} type="submit" color="primary">
                  Log In
                </Button>
              </DialogActions>
            </form>
          </Dialog>

          <Button color="inherit" variant="outlined" onClick={handleClickOpenSign}>
            Sign up
          </Button>
          <Dialog
            open={openSign}
            onClose={handleClickCloseSign}
            aria-labelledby="form-dialog-title"
          >
            <DialogTitle id="form-dialog-title">Authorization</DialogTitle>
            <DialogContent>
              <TextField margin="dense" id="Login" label="Log In" type="text" fullWidth />

              <TextField margin="dense" id="email" label="email" type="email" fullWidth />

              <TextField margin="dense" id="pass" label="Password" type="password" fullWidth />
            </DialogContent>

            <DialogActions>
              <Button onClick={handleClickCloseSign} color="primary">
                Cancel
              </Button>
              <Button color="primary">Sign Up</Button>
            </DialogActions>
          </Dialog>
        </Toolbar>
      </AppBar>
    </Box>
  );
};

export default AuthComponent;
