import { SAVE_MOVIES } from './types';

const initialState = [];

const moviesReducer = (state = initialState, action) => {
  switch (action.type) {
    case SAVE_MOVIES:
      return action.newItems;
    default:
      return state;
  }
};

export default moviesReducer;
