import { CREATE_LIST_ITEM, SAVE_MOVIES } from './types';

const initialState = [];

const moviesReducer = (state = initialState, action) => {
  switch (action.type) {
    case CREATE_LIST_ITEM:
      return [
        ...state,
        {
          id: action.id,
          title_english: action.title_english,
          medium_cover_image: action.medium_cover_image,
          genres: action.genres,
        },
      ];
    case SAVE_LIST:
      return action.newItems;
    default:
      return state;
  }
};

export default moviesReducer;
