import { React, useEffect } from 'react';
import { Card, CardActionArea, CardContent, CardMedia, Container, Typography } from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { saveMovies } from '../../store/actions';
import { useStyles } from './styles.js';
import { makeStyles } from '@mui/styles';

function Content() {
  const classes = useStyles();
  const films = useSelector((state) => state.movie);
  const dispatch = useDispatch();

  function getMovies() {
    fetch('https://yts.mx/api/v2/list_movies.json')
      .then((response) => response.json())
      .then((json) => dispatch(saveMovies(json)));
  }

  useEffect(() => {
    getMovies();
  }, []);

  return (
    <Container>
      {films.map((el) => (
        <Card key={el.id} sx={{ maxWidth: 350, display: 'inline-flex' }}>
          <CardActionArea>
            <CardMedia className={classes.root} component="img" image={el.medium_cover_image} />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {el.title_english}
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
      ))}
    </Container>
  );
}

export default Content;
