import { React, useState } from 'react';
import { Menu } from '@mui/icons-material';
import {
  AppBar,
  Button,
  Container,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  IconButton,
  MenuItem,
  TextField,
  Toolbar,
  Typography,
} from '@mui/material';
import { Box } from '@mui/system';
import { useForm } from 'react-hook-form';
import './AuthStyle.css';

const AuthComponent = () => {
  const pages = ['Products', 'Pricing', 'Blog'];
  const [anchorElNav, setAnchorElNav] = useState(null);
  const [anchorElUser, setAnchorElUser] = useState(null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  const [openAuth, setOpenAuth] = useState(false);
  const [openSign, setOpenSign] = useState(false);

  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm();

  const handleClickOpenAuth = () => {
    setOpenAuth(true);
  };

  const handleClickCloseAuth = () => {
    setOpenAuth(false);
  };

  const handleClickOpenSign = () => {
    setOpenSign(true);
  };

  const handleClickCloseSign = () => {
    setOpenSign(false);
  };

  const onSubmit = (data) => {
    alert(JSON.stringify(data));
    reset();
  };

  return (
    <AppBar position="static">
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          <Typography
            variant="h6"
            noWrap
            component="div"
            sx={{ mr: 2, display: { xs: 'none', md: 'flex' } }}
          >
            LOGO
          </Typography>

          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <Menu />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
              sx={{
                display: { xs: 'block', md: 'none' },
              }}
            >
              {pages.map((page) => (
                <MenuItem key={page} onClick={handleCloseNavMenu}>
                  <Typography textAlign="center">{page}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box>
          <Typography
            variant="h6"
            noWrap
            component="div"
            sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}
          >
            LOGO
          </Typography>
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
            {pages.map((page) => (
              <Button
                key={page}
                onClick={handleCloseNavMenu}
                sx={{ my: 2, color: 'white', display: 'block' }}
              >
                {page}
              </Button>
            ))}
            <Button color="inherit" variant="outlined" onClick={handleClickOpenAuth}>
              Log In
            </Button>

            <Dialog
              open={openAuth}
              onClose={handleClickCloseAuth}
              aria-labelledby="form-dialog-title"
            >
              <form>
                <DialogTitle id="form-dialog-title">Authorization</DialogTitle>
                <DialogContent>
                  <TextField
                    color="primary"
                    margin="dense"
                    id="name"
                    label="Log In"
                    type="login"
                    fullWidth
                    {...register('username', {
                      required: true,
                      minLength: 5,
                      maxLength: 20,
                    })}
                  />

                  <TextField margin="dense" id="pass" label="Password" type="password" fullWidth />
                </DialogContent>

                <DialogActions>
                  <Button onClick={handleClickCloseAuth} color="primary">
                    Cancel
                  </Button>
                  <Button onClick={handleSubmit(onSubmit)} type="submit" color="primary">
                    Log In
                  </Button>
                </DialogActions>
              </form>
            </Dialog>

            <Button color="inherit" variant="outlined" onClick={handleClickOpenSign}>
              Sign up
            </Button>
            <Dialog
              open={openSign}
              onClose={handleClickCloseSign}
              aria-labelledby="form-dialog-title"
            >
              <DialogTitle id="form-dialog-title">Authorization</DialogTitle>
              <DialogContent>
                <TextField margin="dense" id="Login" label="Log In" type="text" fullWidth />

                <TextField margin="dense" id="email" label="email" type="email" fullWidth />

                <TextField margin="dense" id="pass" label="Password" type="password" fullWidth />
              </DialogContent>

              <DialogActions>
                <Button onClick={handleClickCloseSign} color="primary">
                  Cancel
                </Button>
                <Button color="primary">Sign Up</Button>
              </DialogActions>
            </Dialog>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

