import { React, useEffect } from 'react';
import { Card, CardActionArea, CardContent, CardMedia, Container, Typography } from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { saveMovies } from '../../store/actions';

const Content = () => {
  let films = useSelector((state) => state.movie);
  const dispatch = useDispatch();

  function getMovies() {
    fetch('https://yts.mx/api/v2/list_movies.json')
      .then((response) => response.json())
      .then((json) => dispatch(saveMovies(json)));
  }
  films = films.data.movies;
  useEffect(() => {
    getMovies();
  }, []);

  const CustomImage = ({ src }) => (
    <img src={el.medium_cover_image} styles={((width = '230px'), (height = '345'))} />
  );

  return (
    <Container>
      {films.map((el) => {
        return (
          <Card key={el.id} sx={{ maxWidth: 350, display: 'inline-flex' }}>
            <CardActionArea>
              <CardMedia component="img" />
              <CustomImage src={el.medium_cover_image} />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {el.title_english}
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        );
      })}
    </Container>
  );
};

export default Content;
